INSERT INTO `shopcomment` VALUES ('3656', '50287950', '瑞思学科英语西安总部中心', '杰西-J', '', '非常好', '效果:4(非常好)师资:4(非常好)环境:4(非常好)', '\n\n【点评团消费】\n\n\n孩子现在在瑞思上了三个月了。之前孩子很粘我，现在也能每次自己主动要求去瑞思了。之前选机构也跟孩子爸爸看了好多家、后来有朋友推荐瑞思、就会去试听了、现在孩子在瑞思一直很快乐、希望孩子能够一直开心下去…\n', '16-11-04 16:11');
