INSERT INTO `shopcomment` VALUES ('3737', '22152757', '皮个布国际早教(咸宁路中心)', '格格巫12345678', '贡献值50-99', '非常好', '效果:3(很好)师资:3(很好)环境:3(很好)', '\n\n在这里办100节课的卡了，离家比较近，老师对小朋友都不错，宝宝也喜欢去，天天都说要去\n', '16-12-13 23:16');
