INSERT INTO `shopcomment` VALUES ('4226', '14878611', 'A索国际早教(大唐中心店)', '后街歆歆', '', '很差', '', '\n\n差差差，这就是你们表现出来的。本来要给宝宝试听一下全脑的早教课，打电话过去预约，说了是团购的，接电话的人说看下安排稍后会跟我联系，等了一天都没人回电话，这还没掏钱呢都这样，等掏钱了不都打水漂了。还好没在那上！\n', '16-08-21 10:57');
