INSERT INTO `shopcomment` VALUES ('4378', '24856524', '澳洲袋鼠早教', 'L_65592', '', '非常好', '', '\n\n上周带宝宝参加了中心的航海大冒险，宝宝玩的很开心，非常满意！整个中心给人感觉特别的人性化，处处都在为宝宝考虑。之前也在别家早教中心体验过，我个人认为澳洲袋鼠是最专业的！\n', '16-06-29 20:26');
