INSERT INTO `shopcomment` VALUES ('4407', '17669410', 'babyArt创艺宝贝西市城中心', '梦醒时分_926328', '', '非常好', '', '\n\n之前与很多家早教中心比较过，创艺宝贝是最好的，老师们非常热情，上课时非常耐心，在这学习这么长的时间孩子学习做了很多手工，而且还认识了很多小朋友。要想让孩子学习早教的，这儿是不错的选择。\n', '16-07-17 11:01');
