INSERT INTO `shopcomment` VALUES ('4413', '17669410', 'babyArt创艺宝贝西市城中心', '鱼儿雯', '贡献值50-99', '非常好', '', '\n\n看了很多家早教，对于别人评价很高的国际连锁的也有试听，最后选择这里，因为这里的宝贝儿能够享受更好的课程，而且更多的是宝贝儿爱去。每次经过都会说要去上课。很喜欢那里的老师，亲切大方！好评！\n', '16-06-18 15:04');
