INSERT INTO `shopcomment` VALUES ('4425', '17669410', 'babyArt创艺宝贝西市城中心', '醇叁伍12285', '', '非常好', '', '\n\n孩子在这里上了半年了，对孩子的帮助非常大，这里的老师对孩子也非常有耐心，帮家长们解决问题的办法也挺多的，经常会和家长沟通孩子在成长过程中遇到的一些问题，在孩子成长的道路上打下了坚实的基础！最关键的是对孩子去幼儿园起到了铺垫性的新作用，让孩子提前感受群体生活，怎么样和小朋友们和睦相处，点个连环赞！！！\n', '16-04-14 12:16');
