INSERT INTO `shopcomment` VALUES ('4466', '13682687', '西安乐高儿童创意活动中心', 'dang2t', '', '好', '', '\n\n【点评团消费】\n\n\n恩，孩子很喜欢，去过以后就闹着让我给他报名，就是费用有点高，看在孩子实在喜欢，忍了！就是在小区里面不太好找，不过环境还不错。老师们也都非常热心和认真。\n', '13-09-20  更新于13-09-20 18:11');
