INSERT INTO `shopcomment` VALUES ('4577', '21979807', '大庆路小学', '猪家老妹', '贡献值5000以上', '非常好', '', '\n\n这个学校的校纪校风还是很不错的，我们院里大部分孩子都在这所小学就读，平时遇见都十邠日有礼貌，他们学校的课外课堂尤其在足球方面非常强，有一支男队，和一支女队，特别喜欢女队的娃娃们都理个男娃头，暑假天天都要在操场上训练，真地很不简单奥...\n', '16-08-25 21:14');
