INSERT INTO `shopcomment` VALUES ('4731', '11377405', '陕西省西安幼儿园', '石榴结', '贡献值5000以上', '非常好', '效果:4(非常好)师资:4(非常好)环境:4(非常好)', '\n\n创建于1950年，原名“陕西省人民政府保育院”，现直属陕西省妇联领导，是陕西省示范幼儿园，西安市一级幼儿园，首批教育科研先进幼儿园，西安市食品卫生A级单位。\n', '16-09-24 15:06');
